import React, { useState, useEffect } from 'react';
import { Table} from 'antd';
import { Modal } from 'antd';
import axios from 'axios';

// Table columns
const MainPage = () => {
    const columns = [
        {
            title: 'Name',
            dataIndex: 'app_name',
            key: 'app_name',
        },
        {
            title: 'User_name',
            dataIndex: 'username',
            key: 'username',
        },
        {
            title: 'Password',
            dataIndex: 'password',
            key: 'password',
        },
    ];


    const [data, setData] = useState([]);
    const [selectRow, setSelectRow] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        dataFetching();
        console.log(data); // Add this line to check the fetched data
    }, [data]);

    function dataFetching() {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then(response => {
                // Map the API response to fit table columns structure
                const mappedData = response.data.map(item => ({
                    app_name: item.title,        // Mapping title as app_name
                    username: `User ${item.userId}`, // Mocked username
                    password: '******',         // Mocked password
                }));
                setData(mappedData);
            })
            .catch(error => {
                console.error(error);
            });
    }

    const handleRowClick = (record) => {
        setSelectedRow(record);
        setIsModalOpen(true);
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
        setSelectedRow(null);
    };

    return (
        <div>
            <Table
                dataSource={data}
                columns={columns}
                rowKey="app_name"
                onRow={(record) => ({
                    onClick: () => handleRowClick(record),
                })}
            />

        <Modal
            title={selectedRow ? selectedRow.app_name : "Details"}
            open={isModalOpen}
            onOk={handleModalClose}
            onCancel={handleModalClose}
            okText="Save"
            cancelText="Close"
     >
            {selectedRow && (
             <div>
                    <p><strong>Name:</strong> {selectedRow.app_name}</p>
                    <p><strong>User_name:</strong> {selectedRow.username}</p>
                    <p><strong>Password:</strong> {selectedRow.password}</p>
             </div>
            )}
            </Modal>
         </div>
    );

};

export default MainPage;
